<?phpclass Update extends Controller {	function __construct()	{		parent::Controller();	}			function index()	{		$invoices = $this->db->get('invoices');		foreach ($invoices->result() as $invoice) {			$data = array(				   'invoice_id' => $invoice->id,				   'amount' => $invoice->amount,				   'work_description' => $invoice->work_description				);			if ($invoice->tax1_rate > 0) {				$data['taxable'] = 1;			} else {				$data['taxable'] = 0;			}				$this->db->insert('invoice_items', $data); 		}			}}?>